<?php $a="\x80";$b="\x90";function cmp($a,$b) { /* a je mensi nez b */if($a > $b){  $x=put_string($a,">",$b,"\n");}else{}if ($a >= $b ) {  $x=put_string($a,">=",$b,"\n");}else{}if ($a === $b ) {  $x=put_string($a,"===",$b,"\n");}else{}if ($b < $a ) {  $x=put_string($b,"<",$a,"\n");}else{}if ($b <= $a ) {  $x=put_string($b,"<=",$a,"\n");}else{}if ($b === $a ) {  $x=put_string($b,"===",$a,"\n");}else{}if ($a > $a ) {  $x=put_string($a,">",$a,"\n");}else{}if ($a < $a ) {  $x=put_string($a,"<",$a,"\n");}else{}if ($a !== $a ) {  $x=put_string($a,"!==",$a,"\n");}else{}if ($b > $b ) {  $x=put_string($b,">",$b,"\n");}else{}if ($b < $b ) {  $x=put_string($b,"<",$b,"\n");}else{}if ($b !== $b ) {  $x=put_string($b,"!==",$b,"\n");}else{}} /* end function */while($b !== ""){  $x=cmp($a,$b);  $a= get_string();  $b= get_string();}
